use actix_web::{HttpResponse, Responder, web};
use tera::{Context, Tera};

pub async fn page(tera: web::Data<Tera>) -> Result<impl Responder, actix_web::Error> {
    let context = Context::new();
    let rendered = tera.render("index.html", &context).map_err(|e| {
        eprintln!("Failed to render index.html: {}", e);
        actix_web::error::ErrorInternalServerError("Failed to render index.html")
    })?;

    Ok(HttpResponse::Ok().body(rendered))
}

use actix_web::{web, App, HttpServer};

mod pages;

#[ruxt::main]
async fn main() -> std::io::Result<()> {
    let tera = tera::Tera::new("templates/**/*").expect("Failed to compile templates");
    HttpServer::new(move || App::new().app_data(web::Data::new(tera.clone())))
        .bind(("0.0.0.0", 8080))?
        .run()
        .await
}
